select
  t.seisArea as seisArea,
  t.group_name as group_name
  t.seismicParty as seismicParty,
  t.profile_name as profile_name,
  t.picket as picket,
  t.x_deg as x_deg,
  t.y_deg as y_deg,
  t.LastModified as LastModified
from (
select
       SA.NAME as seisArea,
       sp.name || ' ' || p.name as group_name,
       sp.name as seismicParty,
       p.name as profile_name,
       pi.value as picket,
       well.xtob_zone(p_zone_id => sp.zone_id, P_X => pi.x, P_Y => pi.y) as x_deg,
       well.ytol_zone(p_zone_id => sp.zone_id, P_X => pi.x, P_Y => pi.y) as y_deg,
       pi.last_update as LastModified
from well.seis_part sp
left join well.seis_profile p on p.sp_id=sp.id
left join well.seis_picket pi on pi.profile_id=p.id
left join well.seis_part_report spr on spr.sp_id=sp.id
join well.product_document pd on pd.id=spr.report_id
join well.product_document_within_ws pdo on pdo.product_document_id=pd.id
left join wellref.seis_area sa on sa.id=sp.seis_area_id
where p.name != 'KONTUR' and pi.picket_type = 1) t where 1=1


